			function CercleCourberef(){

var Node = document.getElementById("espacecerclesref");
var NodeListe = Node.getElementsByTagName("circle");
  while(Node.hasChildNodes()==true){
    var Enfant=NodeListe.item(0);
    Node.removeChild(Enfant);
  }

