function changeInBaliseTitre(IdBaliseTitre,Texte){
var nt1=document.createTextNode(Texte)
var nt2=document.getElementById(IdBaliseTitre).firstChild
document.getElementById(IdBaliseTitre).replaceChild(nt1,nt2)
}


