function libel(textcompose){ 
//

motcle1=motcle+""// transforme en string
motcle1=motcle1.replace(/\,/g," ")

ope1="ou"

menudata=""
OKITotal=0
OKI=new Array()
if(motcle1==""){OKITotal=1}else{
var motc1=motcle1.split(" ")
var stopmot=motc1.length
for(r=0;r<stopmot;r++){
OKI[r]=0
OKITotal=1
var motc=motc1[r].toLowerCase()
//if(motc=="!revenu"){alert(textcompose.indexOf("!"))}
if(textcompose.toLowerCase().indexOf(motc)>-1 ){
if(ope1=="ou"){OKI[r]=1;OKITotal=OKITotal*OKI[r];r=stopmot}
//if(ope1=="et"){OKI[r]=1;OKITotal=OKITotal*OKI[r]}
}else{
if(ope1=="ou"){OKITotal=0}
//if(ope1=="et"){OKI[r]=0;OKITotal=OKITotal*OKI[r];r=stopmot}
}
}
}
// cas des sauf marqués par un ! en début de mot clé
if(motcle1.indexOf("!")>=0){
for(r=0;r<stopmot;r++){
if(motc1[r].indexOf("!")>=0){
if(textcompose.toLowerCase().indexOf(motc1[r].replace("!",""))>-1 ){OKITotal=0}
}
}
}
OKITotal2=OKITotal


ope1="et"

motcle2=motcleET

menudata=""
OKITotal=0
OKI=new Array()
if(motcle2==""){OKITotal=1}else{
var motc1=motcle2.split("**")
//alert("motc1 dans le et de libel()="+motc1)
var stopmot=motc1.length
OKITotal=1
for(r2=0;r2<stopmot;r2++){
OKI[r2]=0

var motcx=motc1[r2].toLowerCase()
	//alert("motcx="+motcx)





						// supression des doublons d'espace dans le texte
					textcompose=textcompose.replace(/  /g," ")



				// chainage de la chaine recherchée par "_" si elle existe dans le texte
			if(motcx.indexOf("_")>0){
			var motcX=motcx.replace("!","")
					var Xchaine=motcX.split("_")

					var Zchaine=Xchaine[0]
					for(a=1;a<Xchaine.length;a++){ Zchaine+=" "+Xchaine[a]}

					Zchaine=Zchaine.replace(/([.?*+^$[\]\\(){}-])/g,"\\$1")
					var ZZchaine = new RegExp(Zchaine, "g");


					textcompose=textcompose.toLowerCase().replace(ZZchaine,motcX)


			}











if(libel2(textcompose,motcx)==1 ){

if(ope1=="et"){OKI[r2]=1;OKITotal=OKITotal*OKI[r2]}
}else{

if(ope1=="et"){OKI[r2]=0;OKITotal=OKITotal*OKI[r2];r=stopmot}//
}

}
}
//alert(OKITotal2+"  "+OKITotal)
OKITotal=OKITotal2*OKITotal

return OKITotal

}

